mapper.select:
select 
from tb_contract c


		and c.contract_type = #{conditiions.contractType,jdbcType=VARCHAR}


		and c.ORG_ID = #{conditiions.orgId,jdbcType=VARCHAR}


		and  c.location_from = #{conditiions.locationFrom,jdbcType=VARCHAR})


		and  c.location_to = #{conditiions.locationTo,jdbcType=VARCHAR})


		and     regexp_like(c.carrier_name, #{conditiions.carrierName,jdbcType=VARCHAR}) 


		and    regexp_like(c.consign_org_name, #{conditiions.consignOrgName,jdbcType=VARCHAR}) 


		and   date_format(c.contract_start,'%Y-%m-%d') >= #{conditiions.contractStart,jdbcType=VARCHAR} 


		and   date_format(c.contract_end,'%Y-%m-%d') <=  #{conditiions.contractEnd,jdbcType=VARCHAR} 


order by date_format(c.MODIFY_DATE,'%Y-%m-%d %T') desc
